import React, { Component } from "react";
import * as Yup from 'yup';
import {Card, Tab, Nav, Container, Row, Button, Col, ListGroup, Form, InputGroup} from "react-bootstrap";

import PageHeader from "../../components/PageHeader";
import DetailDataLoaderWrapper from "../../components/DataLoaders/DataLoaderDetail";
import FormikForm from "../../components/Forms/FormikForm"


class DealForm extends Component {

    render() {

         const fieldsList = {
          name: {
              title: 'Название сделки',
              sm: 6,
              validation: Yup.string().required('Required')
          },
          date: {
              title: 'Дата',
              sm: 6,
              validation: Yup.date('Incorrect data format').required('Должна быть введена дата')
          },

          client: {
              title: 'Клиент',
              type: 'autocomplete',
              resource: 'clients',
              api: '/api/clients/select/'
          },

          sum_plan: {
              title: 'Сумма',
              sm: 12,
              validation: Yup.number().required()
          },

          google_drive: {
              title: 'Google Drive',
              sm: 12,
              validation: Yup.string()
          },

        };

        return (
            <div>
            <PageHeader
                header = { `Сделка ${this.props.data.name} от ${this.props.data.date}` }
                subHeader =  ""
            />
            <Container fluid style={{ paddingLeft: 40, paddingRight: 40}}>


            <FormikForm fieldList = {fieldsList}
                        onSubmit = {this.onSubmit}
                        initialValues = {this.props.data}
                        onSuccessLink = {this.props.onSuccessLink}
                        {...this.props}
              />
        </Container>
      </div>

    );

    }

};


const Wrapper = DetailDataLoaderWrapper(DealForm, '/api/deals/',  'deals');
export default Wrapper;
